import React, { Component } from 'react'

class Form extends Component {
    state = {
        title: '',
        newId: 3
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.title);
    };
    handleAddTodo = (event) => {
        this.setState((state) => {
            state.todos.push({
                id: state.newId,
                title: event.target.value,
                completed: false,
                editing: false
            })
        })
        // }
    };
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input type="text"
                    onchange={this.handleAddTodo}
                    placeholder="enter to add a todolist"
                />
            </form>)
    };
}

class Item extends React.Component {
    render() {
        const todo = this.props;
        return (
            <li key={todo.id}>{todo.title}</li>
        );
    }
}

const List = (props) => (<label>
    {props.todos.map((todo) => <Item {...todo} />)}</label>
);

class TodoList extends React.Component {
    state = {
        todos: [
            {
                id: 1,
                title: "this is  the first example, double click to edit it",
                completed: false,
                editing: false,
            },
            {
                id: 2,
                title: "this is  the second example, double click to edit it",
                completed: false,
                editing: false,
            },
        ]
    };
    render() {
        return (
            <div>
                <Form />
                <List todos={this.state.todos} />
            </div>
        )
    }
}

export default TodoList
