import React, { Component } from 'react'

class AddandList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: '',
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        this.setState(state => ({
            todos:
                [...state.todos, {
                    id: this.state.newId,
                    title: this.state.currentTitle,
                    completed: false,
                    editing: false
                }],
            newId: state.newId + 1,
            currentTitle: ''
        }));
    };
    render() {
        return (
            <div class="todo-list-frame">
                <form class="add-item-area" onSubmit={this.handleSubmit}>
                    <input type="text"
                        class="add-item-area"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                </form>
                <TodoItem todos={this.state.todos} />
            </div>);
    }
}

class TodoItem extends React.Component {
    render() {
        return (
            <div> {this.props.todos.map((todo) => <div class="todo-item" key={todo.id}><label ondbClick={console.log("hi")}>{todo.title}</label><label onClick={() => this.setState({ todos: this.props.todos.splice(this.props.todos.indexOf(todo), 1) })}>x</label></div>)}</div>
        );
    }
}

class TodoList extends React.Component {
    render() {
        return (
            <div class="main-frame">
                <AddandList />
            </div>
        )
    }
}

export default TodoList
