import React from "react";

import { TodoItem } from "./TodoItem";

class AddandList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      newId: 3,
      currentTitle: "",
      todos: [
        {
          id: 1,
          title: "this is  the first example, double click to edit it",
          completed: false,
          editing: false
        },
        {
          id: 2,
          title: "this is  the second example, double click to edit it",
          completed: false,
          editing: false
        }
      ]
    };
  }
  handleSubmit = event => {
    event.preventDefault();
    console.log(this.state.currentTitle);
    this.setState(state => ({
      todos: [
        ...state.todos,
        {
          id: this.state.newId,
          title: this.state.currentTitle,
          completed: false,
          editing: false
        }
      ],
      newId: state.newId + 1,
      currentTitle: ""
    }));
  };
  handleEditEnter = (currentValue, id) =>
    this.setState(state => {
      const list = state.todos.map(item => ({
        ...item,
        title: item.id === id ? currentValue : item.title
      }));
      console.log(list);
      return list;
    });
  isAllCompleted = state => {
    return state.todos.filter(item => !item.completed).length === 0;
  };
  handleToggleAllItems = () =>
    this.setState(state => {
      state.todos.filter(item => !item.completed).length > 0
        ? state.todos.map(item => {
            item.completed = true;
          })
        : state.todos.map(item => {
            item.completed = false;
          });
    });
  handleChangeCompletedStatus = (currentCompletedStatus, id) =>
    this.setState(state => {
      state.todos.map(item => ({
        ...item,
        completed: item.id === id ? !currentCompletedStatus : item.completed
      }));
    });
  render() {
    return (
      <div className="todo-list-frame">
        <form className="add-item-area" onSubmit={this.handleSubmit}>
          <label className="tick-label" onClick={this.handleToggleAllItems}>
            <i
              class="far fa-circle"
              style={{ display: this.isAllCompleted ? "none" : "block" }}
            ></i>
            <i
              class="fas fa-check"
              style={{ display: this.isAllCompleted ? "block" : "none" }}
            ></i>
          </label>
          <input
            type="text"
            className="add-item-area"
            value={this.state.currentTitle}
            onChange={event =>
              this.setState({ currentTitle: event.target.value })
            }
            placeholder="enter to add a todolist"
            required
          />
        </form>
        <div>
          {this.state.todos.map(todo => (
            <TodoItem
              todo={todo}
              handleDelete={() =>
                this.setState(state => ({
                  todos: state.todos.filter(item => item.id !== todo.id)
                }))
              }
              handleEditEnter={this.handleEditEnter}
              isAllCompleted={this.isAllCompleted}
              handleChangeCompletedStatus={this.handleChangeCompletedStatus}
            />
          ))}
        </div>
      </div>
    );
  }
}
class TodoList extends React.Component {
  render() {
    return (
      <div className="main-frame">
        <AddandList />
      </div>
    );
  }
}

export default TodoList;
