import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
export class TodoItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentId: "",
      currentValue: ""
    };
  }
  handleChange = event => {
    this.setState({ currentValue: event.target.value });
  };

  handleTextChanged = event => {
    if (event.key === "Enter") {
      this.props.handleEditEnter(this.state.currentValue, this.props.todo.id);
      this.setState({ currentId: -1 });
      console.log("haha, entered");
    }
  };

  render() {
    return (
      <div className="todo-item" key={this.props.todo.id}>
        <label
          className="tick-label"
          onClick={() =>
            this.props.handleChangeCompletedStatus(
              this.props.todo.completed,
              this.props.todo.id
            )
          }
        >
          <FontAwesomeIcon
            icon="circle"
            style={{ display: this.props.todo.completed ? "none" : "block" }}
          />
          <FontAwesomeIcon
            icon="check-circle"
            style={{ display: this.props.todo.completed ? "block" : "none" }}
          />
        </label>
        <input
          className="todo-item-checkbox-input"
          type="checkbox"
          checked={this.props.todo.completed}
        />{" "}
        {this.state.currentId === this.props.todo.id ? (
          <input
            type="text"
            className="todo-item-input"
            value={this.state.currentValue}
            onChange={this.handleChange}
            onKeyPress={this.handleTextChanged}
          />
        ) : (
          <label
            className="todo-item-text"
            onDoubleClick={event => {
              event.preventDefault();
              this.setState({
                currentId: this.props.todo.id,
                currentValue: this.props.todo.title
              });
            }}
          >
            {this.props.todo.title}
            <label onClick={this.props.handleDelete}> x</label>
          </label>
        )}
      </div>
    );
  }
}
