import React, { Component } from 'react'

class Form extends Component {
    state = { title: '' }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.userName);
    };
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input type="text"
                    onchange={this.handleAddTodo}
                    placeholder="enter to add a todolist"
                />
            </form>)
    };
}

const List = (props) => (<label>
    {props.todos.map((todo) => <li key={todo.id}>{todo.title}</li>)}</label>
);


class TodoList extends React.Component {
    state = {
        newId: 3,
        todos: [
            {
                id: 1,
                title: "this is  the first example, double click to edit it",
                completed: false,
                editing: false,
            },
            {
                id: 2,
                title: "this is  the second example, double click to edit it",
                completed: false,
                editing: false,
            },
        ]
    };
    render() {
        return (
            <div>
                <Form onKeyPress={(event) => this.handleAddTodo(event)} placeholder="input here to add a todo-item" />
                <List todos={this.state.todos} />
            </div>
        )
    }
}

export default TodoList
