import React, { Component } from 'react'

class TodoList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }

    handleAddTodo(event) {
        if (event.keyCode !== 13) {
            return;
        }
        else {
            this.setState(state => {
                state.todos.push({
                    id: state.newId,
                    title: event.target.value,
                    completed: false,
                    editing: false
                });
            })
        }
    };

    render() {
        const todoList = this.state.todos.map((todo) => <li key={todo.id}>{todo.title}</li>);

        return (
            <div>
                <input onKeyPress="handleAddTodo(event)" placeholder="input here to add a todo-item" />
                <label> {todoList} </label>
            </div>
        )
    }
}

export default TodoList
