import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: '',
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        // this.props.todos.push({
        //     id: this.state.newId,
        //     title: this.state.currentTitle,
        //     completed: false,
        //     editing: false
        // });
        this.setState({
            todos: () => this.state.todos.push({
                id: this.state.newId,
                title: this.state.currentTitle,
                completed: false,
                editing: false
            }),
            newId: this.state.newId + 1,
            currentTitle: ''
        });
    };
    render() {
        return (
            <div class="todo-list-frame">
                <form class="add-item-area" onSubmit={this.handleSubmit}>
                    <input type="text"
                        class="add-item-area"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                </form>
                <label >
                    <Item {...todo} todo={this.state.todos} />
                </label></div>);
    }
}

class Item extends React.Component {
    render() {
        const todo = props.todos;
        return (
            <li class="todo-item" key={todo.id}>{todo.title}</li>
        );
    }
}

class TodoList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    render() {
        return (
            <div class="main-frame">
                <Form />
            </div>
        )
    }
}

export default TodoList
