import React, { Component } from 'react'

class AddandList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: '',
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        this.setState(state => ({
            todos:
                [...state.todos, {
                    id: this.state.newId,
                    title: this.state.currentTitle,
                    completed: false,
                    editing: false
                }],
            newId: state.newId + 1,
            currentTitle: '',
        }));
    };
    handleEditEnter = (currentValue, id) => this.setState(state => {
        state.todos.map(item => item.id === id ? item.title = currentValue : item)
    });
    isAllCompleted = (state) => {
        return state.todos.filter(item => !item.completed).length === 0;
    };
    handleChangeCompletedStatus = (currentCompletedStatus, id) =>
        this.setState(state => {
            state.todos.map(item => item.id === id ? item.completed = !currentCompletedStatus : item)
        });
    render() {
        return (
            <div className="todo-list-frame">
                <form className="add-item-area" onSubmit={this.handleSubmit}>
                    <input type="text"
                        className="add-item-area"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                </form>
                <div>{this.state.todos.map(todo => (<TodoItem todo={todo} handleDelete={() => this.setState(state => ({ todos: state.todos.filter(item => item.id !== todo.id) }))} handleEditEnter={this.handleEditEnter} isAllCompleted={this.isAllCompleted} handleChangeCompletedStatus={this.handleChangeCompletedStatus} />))}</div>
            </div >);
    }
}

class TodoItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentId: '',
            currentValue: ''
        }
    }
    handleChange = (event) => {
        this.setState({ currentValue: event.target.value });
    }

    render() {
        return (
            <div className="todo-item" key={this.props.todo.id} ><label className="tick-label" onClick={this.props.handleChangeCompletedStatus(this.props.todo.completed, this.props.todo.id)}><i className="far fa-circle" style={{ display: this.props.todo.completed ? 'none' : 'block' }}></i><i className="far fa-check-circle" style={{ display: this.props.todo.completed ? 'block' : 'none' }}></i></label ><input className="todo-item-checkbox-input" type="checkbox" /> {(this.state.currentId === this.props.todo.id) ? <input type="text" className="todo-item-input" value={this.state.currentValue} onChange={this.handleChange} onKeyPress={event => { if (event.key === 'Enter') { this.props.handleEditEnter(this.state.currentValue, this.props.todo.id); this.setState({ currentId: -1 }) } }} /> : <label className="todo-item-text" onDoubleClick={event => { event.preventDefault(); this.setState({ currentId: this.props.todo.id, currentValue: this.props.todo.title }) }}>{this.props.todo.title}<label onClick={this.props.handleDelete}> x</label></label>
            }</div>
        );
    }
}

class TodoList extends React.Component {
    render() {
        return (
            <div className="main-frame">
                <AddandList />
            </div>
        )
    }
}

export default TodoList
