import React, { Component } from 'react'

class AddandList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: '',
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        this.setState(state => ({
            todos:
                [...state.todos, {
                    id: this.state.newId,
                    title: this.state.currentTitle,
                    completed: false,
                    editing: false
                }],
            newId: state.newId + 1,
            currentTitle: ''
        }));
    };
    handleDelete = (event) => {
        event.preventDefault();
        this.setState((state) => ({ todos: state.todos.filter((item, id) => (item.id !== id)) }))
    };

    render() {
        return (
            <div className="todo-list-frame">
                <form className="add-item-area" onSubmit={this.handleSubmit}>
                    <input type="text"
                        className="add-item-area"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                </form>
                <div>{this.state.todos.map(todo => (<TodoItem id={todo.id} title={todo.title} completed={todo.completed} editing={todo.editing} handleDelete={() => handleDelete(todo.id)} />))}</div>
            </div >);
    }
}

class TodoItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentId: '',
            currentValue: ''
        }
    }
    handleChange = (event) => {
        this.setState({ currentValue: event.target.value });
    }
    handleEnter = (event) => {
        if (event.key === 'Enter') {
            this.setState({
                currentId: -1
            });
        }
    }
    render() {
        return (
            <div className="todo-item" key={this.props.id} onDoubleClick={event => { event.preventDefault(); this.setState({ currentId: this.props.id, currentValue: this.props.title }) }}>{(this.state.currentId === this.props.id) ? <input type="text" className="todo-item-input" value={this.state.currentValue} onChange={this.handleChange} onKeyPress={this.handleEnter} /> : <label className="todo-item-text">{this.props.title}<label onClick={this.props.handleDelete}> x</label></label>}</div>
        );
    }
}

class TodoList extends React.Component {
    render() {
        return (
            <div className="main-frame">
                <AddandList />
            </div>
        )
    }
}

export default TodoList
