import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // todo: {
            //     id: '',
            //     title: '',
            //     completed: '',
            //     editing: ''
            // },
            newId: 3,
            currentTitle: ''
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.todo.currentTitle);
        // this.setState({
        //     todo:
        //     {
        //         id: this.state.newId,
        //         title: this.state.currentTitle,
        //         completed: false,
        //         editing: false
        //     },
        //     newId: this.state.newId++,
        // });
        this.props.todos.push({
            id: this.state.newId,
            title: this.state.currentTitle,
            completed: false,
            editing: false
        });
        this.setState({
            newId: this.state.newId++,
            currentTitle: ''
        });
    };
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input type="text"
                    value={this.state.currentTitle}
                    onChange={event =>
                        this.setState(
                            { currentTitle: event.target.value }
                        )
                    }
                    placeholder="enter to add a todolist"
                    required
                />
                <button>submit</button>
            </form>);
    }
}

class Item extends React.Component {
    render() {
        const todo = this.props;
        return (
            <li key={todo.id}>{todo.title}</li>
        );
    }
}

const List = (props) => (<label>
    {props.todos.map((todo) => <Item {...todo} />)}</label>
);

class TodoList extends React.Component {
    state = {
        todos: [
            {
                id: 1,
                title: "this is  the first example, double click to edit it",
                completed: false,
                editing: false,
            },
            {
                id: 2,
                title: "this is  the second example, double click to edit it",
                completed: false,
                editing: false,
            },
        ]
    };
    render() {
        return (
            <div>
                <Form todos={this.state.todos} />
                <List todos={this.state.todos} />
            </div>
        )
    }
}

export default TodoList
