import React, { Component } from 'react'

class TodoList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }

    handleAddTodo = (e) => {
        this.setState({ text: e.target.value });

    };

    render() {
        const todoList = this.state.todos.map((todo) => <li key={todo.id}>{todo.title}</li>);

        return (
            <div>
                <input onClick="handleAddTodo" placeholder="input here to add a todo-item" />
                <label> {todoList} </label>
            </div>
        )
    }
}

export default TodoList
