import React, { useState } from "react";
import { EditItemArea } from "./EditItemArea";

export const FILTER_STATUS = {
  ALL: "all",
  UNCOMPLETED: "uncompleted",
  COMPLETED: "completed",
};

export function FilterClearAndEditItemsArea(props) {
  const [filterKey, setFilterKey] = useState(FILTER_STATUS.ALL);

  const getTodosByFilterKey = () => {
    const isFilterCompleted = filterKey === FILTER_STATUS.COMPLETED;
    return filterKey === FILTER_STATUS.ALL
      ? props.todos
      : props.todos.filter((todo) => todo.completed === isFilterCompleted);
  };

  const handleChangeFilterKey = (filterRadio) => setFilterKey(filterRadio);

  return (
    <div>
      {getTodosByFilterKey().map((todo) => (
        <EditItemArea
          key={todo.id}
          todo={todo}
          handleItemDelete={props.handleItemDelete}
          handleEditEnter={props.handleEditEnter}
          handleChangeCompletedStatus={props.handleChangeCompletedStatus}
        />
      ))}
      <FilterClearItemsArea
        filterKey={filterKey}
        handleChangeFilterKey={handleChangeFilterKey}
        getTodosByFilterKey={getTodosByFilterKey}
      />
    </div>
  );
}

function FilterClearItemsArea(props) {
  const CLEAR_BUTTON = "clear completed";

  const filterRadios = Object.values(FILTER_STATUS);

  const getButtonStyleByFilterKey = (filterRadio) => {
    return props.filterKey === filterRadio ? "filtered" : "filter-li";
  };

  const notAllItemInfo =
    props.filterKey === FILTER_STATUS.ALL
      ? ""
      : `${props.filterKey} items:${props.getTodosByFilterKey().length}`;

  const jointedItemInfo = `${FILTER_STATUS.ALL} items:${props.todos.length} ${notAllItemInfo}`;

  const isNeededClear = () => {
    return props.todos.filter((todo) => todo.completed).length > 0;
  };

  const showClearButton = {
    visibility: isNeededClear() ? "visible" : "hidden",
  };

  return (
    <div className="filter-item-area">
      <span className="remaining-span">{jointedItemInfo}</span>
      <span className="filter-span">
        {filterRadios.map((filterRadio, index) => (
          <li
            className={getButtonStyleByFilterKey(filterRadio)}
            key={index}
            value={filterRadio}
            onClick={() => handleChangeFilterKey(filterRadio)}
          >
            {filterRadio}
          </li>
        ))}
      </span>
      <span className="clear-span">
        <li
          className="clear-li"
          style={showClearButton}
          onClick={() => props.handleClearCompleted()}
        >
          {CLEAR_BUTTON}
        </li>
      </span>
    </div>
  );
}
