import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: ''
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        this.props.todos.push({
            id: this.state.newId,
            title: this.state.currentTitle,
            completed: false,
            editing: false
        });
        this.setState({
            newId: this.state.newId + 1,
            currentTitle: ''
        });
    };
    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                    <button>add</button>
                </form>
                <label class="todo-item">
                    {this.props.todos.map((todo) => <Item {...todo} />)}</label></div>);
    }
}

class Item extends React.Component {
    render() {
        const todo = this.props;
        return (
            <li class="todo-item-text" key={todo.id}>{todo.title}</li>
        );
    }
}

class TodoList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    render() {
        return (
            <div>
                <Form todos={this.state.todos} />
            </div>
        )
    }
}

export default TodoList
