import React, { Component } from 'react'

class AddandList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: '',
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        this.setState(state => ({
            todos:
                [...state.todos, {
                    id: this.state.newId,
                    title: this.state.currentTitle,
                    completed: false,
                    editing: false
                }],
            newId: state.newId + 1,
            currentTitle: ''
        }));
    };
    render() {
        return (
            <div class="todo-list-frame">
                <form class="add-item-area" onSubmit={this.handleSubmit}>
                    <input type="text"
                        class="add-item-area"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                </form>
                <TodoItem todos={this.state.todos} />
            </div>);
    }
}

class TodoItem extends React.Component {
    // const todos = Array.from(props.todos);
    // const items = todos.map((todo) => <div class="todo-item" key={todo.id}><label>{todo.title}</label><label className={todo.id} onClick={handleDeleteClick}>x</label></div>);
    // handleDeleteClick = () => {
    //     event.preventDefault();
    //     const selectedId = event.target.getAttribute('class');
    //     var stateTodo = todos.find(todo => todo.id === selectedId);
    //     todos.splice(todos.indexOf(stateTodo), 1);
    // };
    render() {
        const todos = this.props;
        return (
            <div> {todos.map((todo) => <div class="todo-item" key={todo.id}><label>{todo.title}</label><label className={todo.id} onClick={todo => this.setState({ todos: props.todos.splice(props.todos.indexOf(todo.id), 1) })}>x</label></div>)}</div >
        );
    }
}

class TodoList extends React.Component {
    render() {
        return (
            <div class="main-frame">
                <AddandList />
            </div>
        )
    }
}

export default TodoList
