import React, { Component } from 'react'

class AddandList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: '',
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        this.setState(state => ({
            todos:
                [...state.todos, {
                    id: this.state.newId,
                    title: this.state.currentTitle,
                    completed: false,
                    editing: false
                }],
            newId: state.newId + 1,
            currentTitle: ''
        }));
    };
    render() {
        return (
            <div class="todo-list-frame">
                <form class="add-item-area" onSubmit={this.handleSubmit}>
                    <input type="text"
                        class="add-item-area"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                </form>
                <TodoItem todos={this.state.todos} />
            </div>);
    }
}

function TodoItem(props) {
    const todos = Array.from(props.todos);
    const items = todos.map((todo) => <div class="todo-item" key={todo.id}><TextArea /><DeleteArea onClick={handleDeleteClick} /></div>);
    const TextArea = <label>{this.props.item.title}</label>;
    const DeleteArea = <label className={this.props.item.id}>x</label>;


    const handleDeleteClick = (e) => {
        this.setState((todos, selectedId) => {
            selectedId = e.target.label.getAttribute('name');
            var stateTodo = todos.find(todo => todo.id === selectedId);
            todos.splice(todos.indexOf(stateTodo), 1);
        });
    };
    return (
        <div>{items}</div>
    );
}

class TodoList extends React.Component {
    render() {
        return (
            <div class="main-frame">
                <AddandList />
            </div>
        )
    }
}

export default TodoList
