import React, { Component } from 'react'

class AddForm extends Component {
    constructor(props) {
        super(props);

        this.handleAddTodo = this.handleAddTodo.bind(this);
    }

    handleAddTodo = (event) => {
        // if (event.keyCode !== 13) {
        //     return;
        // }
        // else {
        this.setState((state) => {
            state.todos.push({
                id: state.newId,
                title: "test",
                completed: false,
                editing: false
            })
        })
        // }
    };
};

const List = (props) => (<label>
    {props.todos.map((todo) => <li key={todo.id}>{todo.title}</li>)}</label>
);




class TodoList extends React.Component {
    state = {
        newId: 3,
        todos: [
            {
                id: 1,
                title: "this is  the first example, double click to edit it",
                completed: false,
                editing: false,
            },
            {
                id: 2,
                title: "this is  the second example, double click to edit it",
                completed: false,
                editing: false,
            },
        ]
    };
    render() {
        const todoList = this.state.todos.map((todo) => <li key={todo.id}>{todo.title}</li>);

        return (
            <div>
                <input onKeyPress={(event) => this.handleAddTodo(event)} placeholder="input here to add a todo-item" />
                <List todos={this.state.todos} />
            </div>
        )
    }
}

export default TodoList
