import React, { Component } from 'react'

class TodoStore extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }

    render() {
        const todoList = this.state.todos.map((todo) => <li>{todo.title}</li>);
        return (
            <div key={todo.id}>
                <ul> {todoList} </ul>
            </div>
        )
    }
}

export default TodoStore
