import React, { Component } from 'react'

class Form extends Component {
    state = {
        title: '',
        newId: '',
        completed: '',
        editing: ''
    };
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.title);
        this.setState({
            id: state.newId,
            title: state.title,
            completed: false,
            editing: false
        })

    };
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input type="text"
                    value={this.state.title}
                    onChange={event =>
                        this.setState({
                            title: event.target.value
                        })
                    }
                    placeholder="enter to add a todolist"
                    required
                />
                <button>submit</button>
            </form>);
    }
}

class Item extends React.Component {
    render() {
        const todo = this.props;
        return (
            <li key={todo.id}>{todo.title}</li>
        );
    }
}

const List = (props) => (<label>
    {props.todos.map((todo) => <Item {...todo} />)}</label>
);

class TodoList extends React.Component {
    state = {
        todos: [
            {
                id: 1,
                title: "this is  the first example, double click to edit it",
                completed: false,
                editing: false,
            },
            {
                id: 2,
                title: "this is  the second example, double click to edit it",
                completed: false,
                editing: false,
            },
        ]
    };
    render() {
        return (
            <div>
                <Form todos={this.state.todos} />
                <List todos={this.state.todos} />
            </div>
        )
    }
}

export default TodoList
