import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newId: 3,
            currentTitle: '',
            todos: [
                {
                    id: 1,
                    title: "this is  the first example, double click to edit it",
                    completed: false,
                    editing: false,
                },
                {
                    id: 2,
                    title: "this is  the second example, double click to edit it",
                    completed: false,
                    editing: false,
                },
            ]
        };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.currentTitle);
        this.setState(state => ({
            todos:
                [...state.todos, {
                    id: this.state.newId,
                    title: this.state.currentTitle,
                    completed: false,
                    editing: false
                }],
            newId: this.state.newId + 1,
            currentTitle: ''
        }));
    };
    render() {
        return (
            <div class="todo-list-frame">
                <form class="add-item-area" onSubmit={this.handleSubmit}>
                    <input type="text"
                        class="add-item-area"
                        value={this.state.currentTitle}
                        onChange={event =>
                            this.setState(
                                { currentTitle: event.target.value }
                            )
                        }
                        placeholder="enter to add a todolist"
                        required
                    />
                </form>
                <Item todos={this.state.todos} />
            </div>);
    }
}

function Item(props) {
    const todos = Array.from(props.todos);
    const item = todos.map((todo) => <li class="todo-item" key={todo.id}>{todo.title}</li>);
    console.log(item);
    return (
        <label >{item}</label>
    );
}

