import React, { useState } from "react";

export function TodoItem(props) {
  const [editStatus, setEditState] = useState(false);
  const [currentValue, setCurrentValue] = useState("");
  const itemLabelStyle = {
    color: props.todo.completed ? "lightgrey" : "black",
  };

  const showItemByEditStatus = () => {
    editStatus ? (
      <input
        type="text"
        className="todo-item-input"
        value={currentValue}
        onChange={handleEditChange}
        onKeyDown={handleEditKeyDown}
        onBlur={handleEditKeyEnter}
        autoFocus="true"
      />
    ) : (
      <label
        className="todo-item-text"
        onDoubleClick={handleItemDoubleClicked}
        style={itemLabelStyle}
      >
        {props.todo.title}
      </label>
    );
  };

  const handleItemDoubleClicked = (event) => {
    event.preventDefault();
    setEditState(true);
    setCurrentValue(props.todo.title);
  };

  const handleEditChange = (event) => {
    event.preventDefault();
    setCurrentValue(event.target.value);
  };

  const handleEditKeyDown = (event) => {
    const enterKey = 13;
    const escapeKey = 27;
    if (event.keyCode === enterKey) {
      handleEditKeyEnter();
    } else if (event.keyCode === escapeKey) {
      setEditState(false);
    }
  };

  const handleEditKeyEnter = () => {
    props.handleEditEnter(currentValue, props.todo.id);
    setEditState(false);
  };

  return (
    <div className="todo-item-area" key={props.todo.id}>
      <label className="todo-item">
        <label className="todo-item-label">
          <CheckItemIcon
            handleChangeCompletedStatus={props.handleChangeCompletedStatus}
            todo={props.todo}
          />
          <input className="todo-item-checkbox-input" type="checkbox" />
          {showItemByEditStatus}
        </label>
        <DeleteItemIcon handleItemDelete={props.handleItemDelete} />
      </label>
    </div>
  );
}

function CheckItemIcon(props) {
  const className = props.todo.completed
    ? "far fa-check-circle"
    : "far fa-circle";
  return (
    <label className="tick-label" onClick={props.handleChangeCompletedStatus}>
      <i className={className}></i>
    </label>
  );
}

function DeleteItemIcon(props) {
  const DELETE = "x";
  return (
    <label className="delete-label" onClick={props.handleItemDelete}>
      {DELETE}
    </label>
  );
}
